import React, { useState, useEffect } from "react";
import { webData } from "../data";
import "./Body.css";
import Merchant from "./Merchant";

function Body() {
  const [categoryNameNow, setCategoryNameNow] = useState();
  const [categoryNow, setCategoryNow] = useState();

  const [provinceNow, setProvinceNow] = useState();
  const [priceNow, setPriceNow] = useState();

  // display subcategoy for each categotr you chose
  useEffect(() => {
    switch (categoryNameNow) {
      case "ร้านอาหารและเครื่องดื่ม":
        setCategoryNow(webData.categories[0]);

        break;
      case "ร้านค้า OTOP":
        setCategoryNow(webData.categories[1]);

        break;
      case "ร้านธงฟ้า":
        setCategoryNow(webData.categories[2]);

        break;
      case "สินค้าทั่วไป":
        setCategoryNow(webData.categories[3]);

        break;
      default:
        setCategoryNow();
    }
  }, [categoryNameNow]);

  const categoryChangeHandler = e => {
    console.log("cate --- , ", e.target.value);
    setCategoryNameNow(e.target.value);
  };

  const subcategoryChangeHandler = e => {
    console.log("subcate, ", e.target.value);
    //setSubCateNow(e.target.value);
  };

  const categories = webData.categories;
  const provinces = webData.provinces;
  const priceRange = webData.priceRange;
  const merchants = webData.merchants;

  let eachCate = categories.map(category => {
    return (
      <div>
        <input
          type="radio"
          name="chooseOneCategory"
          value={category.name}
          onClick={categoryChangeHandler}
        ></input>
        <span>{category.name}</span>
      </div>
    );
  });

  let categoryBody = (
    <div>
      <input
        type="radio"
        name="chooseOneCategory"
        value="ทั้งหมด"
        onClick={categoryChangeHandler}
      ></input>
      <span>ทั้งหมด</span>
      {eachCate}
    </div>
  );

  let provinceBody = provinces.map(province => {
    return <option>{province}</option>;
  });

  let priceBody = priceRange.map(price => {
    return <option>{price}</option>;
  });

  let eachsubcate = categoryNow.subcategories.map(subcate => {
    return (
      <div>
        <input
          type="radio"
          name="chooseOneSubCategory"
          value={subcate}
          onClick={subcategoryChangeHandler}
        ></input>
        <span>{subcate}</span>
      </div>
    );
  });

  let subcategoryBody = (
    <div>
      <input
        type="radio"
        name="chooseOneSubCategory"
        value="ทั้งหมด"
        onClick={subcategoryChangeHandler}
      ></input>
      <span>ทั้งหมด</span>
      {eachsubcate}
    </div>
  );

  let merchantBody = merchants.map(merchant => {
    return <Merchant merchant={merchant}></Merchant>;
  });

  return (
    <div className="grid">
      <div className="leftBody">
        <div>ประเภทร้านค้า</div>
        {categoryBody}
        <div>
          จังหวัดใกล้ฉัน
          <select>
            <option value="0">พื้นที่ใกล้ฉัน</option>
            {provinceBody}
          </select>
        </div>
        <div>
          ราคา
          <select>
            <option value="0">กรุณาเลือก</option>
            {priceBody}
          </select>
        </div>
        <div>
          ประเภท{categoryNameNow}
          {subcategoryBody}
        </div>
      </div>
      {/* <div className="rightBody">{merchantBody}</div> */}
      <div className="rightBody">
        {/* <input type="checkbox" onChange={checkboxHandler}></input> */}
      </div>
    </div>
  );
}

export default Body;
