import React, { useState, useEffect } from "react";
import "./Merchant.css";
import car from "./icon/car.png";

function Merchant(props) {
  //const merchant = props.merchant;
  console.log("props", props);
  const coverImageId = props.merchant.coverImageId;
  const shopNameTH = props.merchant.shopNameTH;
  const isOpen = props.merchant.isOpen;
  const categoryName = props.merchant.categoryName;
  const subcategoryName = props.merchant.subcategoryName;
  const facilities = props.merchant.facilities;
  const priceLevel = props.merchant.priceLevel;
  const highlightText = props.merchant.highlightText;
  const recommendedItems = props.merchant.recommendedItems;
  const addressProvinceName = props.merchant.addressProvinceName;
  const addressDistrictName = props.merchant.addressDistrictName;

  //   return (
  //     <div className="rowMerchant">
  //       <div>
  //         <img className="imgMerchant" src={`${coverImageId}`}></img>
  //       </div>
  //       <div className="rightMerchant">
  //         <div style={{ color: "black" }}>{shopNameTH}</div>
  //         {/* <div style={{ color: "black" }}>{isOpen}</div>
  //         <div style={{ color: "black" }}>{categoryName}</div>
  //         <div style={{ color: "black" }}>{subcategoryName}</div>
  //         <div style={{ color: "black" }}>{facilities}</div>
  //         <div style={{ color: "black" }}>{priceLevel}</div>
  //         <div style={{ color: "black" }}>{highlightText}</div>
  //         <div style={{ color: "black" }}>{recommendedItems}</div>
  //         <div style={{ color: "black" }}>{addressDistrictName}</div>
  //         <div style={{ color: "black" }}>{addressProvinceName}</div> */}
  //       </div>
  //     </div>
  //   );

  const [isOpenM, setisOpenM] = useState();

  const [priceLevelMerchant, setPriceLevelMerchant] = useState(0);

  let isOpenText;
  if (isOpen === "Y") {
    isOpenText = (
      <div className="isOpenText" style={{ color: "green" }}>
        เปิดอยู่
      </div>
    );
  } else if (isOpen === "N") {
    isOpenText = (
      <div className="isOpenText" style={{ color: "red" }}>
        ปิดอยู่
      </div>
    );
  } else {
    isOpenText = <div></div>;
  }

  let levelOfPrice = [];
  for (let i = 0; i < 4; i++) {
    if (i < priceLevel) {
      levelOfPrice.push(<span>฿</span>);
    } else {
      levelOfPrice.push(<span style={{ color: "gray" }}>฿</span>);
    }
  }
  let facilitiesM = [];
  for (let i = 0; i < facilities.length; i++) {
    facilitiesM.push(<span>{facilities[i]}</span>);
    facilitiesM.push(<span> </span>);
  }

  let additionalInfo;
  if (isOpen !== "N/A") {
    additionalInfo = (
      <div>
        {" "}
        <div>{highlightText}</div>
        <div>เมนูแนะนำ: {recommendedItems}</div>
        <div style={{ color: "green" }}>{facilitiesM}</div>
      </div>
    );
  }

  return (
    <div className="merchantBox">
      <img className="merchantImg" src={`${coverImageId}`}></img>
      {/* <img className="iconImg" src={car}></img> */}
      <div className="merchantText">
        <div>
          {shopNameTH} {isOpenText}
        </div>
        <div>
          {subcategoryName} | {levelOfPrice}| {addressDistrictName}{" "}
          {addressProvinceName}
        </div>
        <div>
          ------------------------------------------------------------------------
        </div>
        {additionalInfo}
      </div>
    </div>
  );
}

export default Merchant;
