import React, { useState, useEffect } from "react";
import { webData } from "../data";
import "./Body.css";
import Merchant from "./Merchant";

function Body() {
  const [cateNow, setCateNow] = useState();
  const [subCateNow, setSubCateNow] = useState([]);
  const [provinceNow, setProvinceNow] = useState();
  const [priceNow, setPriceNow] = useState();

  useEffect(() => {
    switch (cateNow) {
      case "ร้านอาหารและเครื่องดื่ม":
        setSubCateNow(webData.categories[0].subcategories);
        break;
      case "ร้านค้า OTOP":
        setSubCateNow(webData.categories[1].subcategories);
        break;
      case "ร้านธงฟ้า":
        setSubCateNow(webData.categories[2].subcategories);
        break;
      case "สินค้าทั่วไป":
        setSubCateNow(webData.categories[3].subcategories);
        break;
      default:
        setSubCateNow(webData.categories[0].subcategories);
    }
  }, [cateNow]);

  const categoryChangeHandler = e => {
    setCateNow(e.target.value);
  };

  // const subcategoryChangeHandler = e => {
  //   setSubCateNow(e.target.value);
  // };

  const categories = webData.categories;
  const provinces = webData.provinces;
  const priceRange = webData.priceRange;
  const merchants = webData.merchants;

  let eachCate = categories.map(category => {
    return (
      <div>
        <input
          type="radio"
          name="chooseOneCategory"
          value={category.name}
          onClick={categoryChangeHandler}
        ></input>
        <span>{category.name}</span>
      </div>
    );
  });

  let categoryBody = (
    <div>
      <input
        type="radio"
        name="chooseOneCategory"
        value="AllCate"
        onClick={categoryChangeHandler}
      ></input>
      <span>ทั้งหมด</span>
      {eachCate}
    </div>
  );

  let provinceBody = provinces.map(province => {
    return <option>{province}</option>;
  });

  let priceBody = priceRange.map(price => {
    return <option>{price}</option>;
  });

  // depends on which category
  let subcatBody = subCateNow.map(subcat => {
    return (
      <div>
        <input
          type="radio"
          name="chooseOneSubCategory"
          value={subcat}
          onClick={subcategoryChangeHandler}
        ></input>
        <span>{subcat}</span>
      </div>
    );
  });

  let merchantBody = merchants.map(merchant => {
    return <Merchant merchant={merchant}></Merchant>;
  });

  return (
    <div className="grid">
      <div className="leftBody">
        <div>ประเภทร้านค้า</div>
        {categoryBody}
        <div>
          จังหวัดใกล้ฉัน
          <select>
            <option value="0">พื้นที่ใกล้ฉัน</option>
            {provinceBody}
          </select>
        </div>
        <div>
          ราคา
          <select>
            <option value="0">กรุณาเลือก</option>
            {priceBody}
          </select>
        </div>
        <div>
          ประเภทร้านอาหารและเครื่องดื่ม
          {subcatBody}
        </div>
      </div>
      {/* <div className="rightBody">{merchantBody}</div> */}
      <div className="rightBody">
        {/* <input type="checkbox" onChange={checkboxHandler}></input> */}
      </div>
    </div>
  );
}

export default Body;
